ut['id'] = 'email';	$input['size'] = '30';	$input['maxlength'] = '64';	$input['tabindex'] = $t;	$input['autocomplete'] = 'off';	$input['value'] = @set_value('email', $user->email);	echo form_input($input);	$t++;	?>	</div><hr><div class="alpha three columns"><h6>Quota</h6></div><div class="omega nine columns">	<?php	unset($input);	$input['accesskey'] = 'Q';	$input['name'] = 'quota';	$input['id'] = 'quota';	$input['size'] = '5';	$input['maxlength'] = '5';	$input['tabindex'] = $t;	$input['autocomplete'] = 'off';	$input['value'] = @set_value('quota', $user->quota);	echo form_input($input);	$t++;	?>	</div><hr><div class="alpha three columns"><h6>Options</h6></div><div class="omega nine columns">		<fieldset>		<label for="enabled">			<?php			echo form_hidden('enabled', '0');			unset($input);			$input['name'] = 'enabled';			$input['id'] = 'enabled';			$input['value'] = '1';			$input['checked'] = @set_checkbox($input['name'], $input['value'], 				($user->enabled == $input['value']));			$input['tabindex'] = $t;			echo form_checkbox($input);			$t++;			?>			<span>Enable account</span>		</label>	</fieldset>		<fieldset>		<label for="ldap">			<?php			echo form_hidden('ldap', '0');			unset($input);			$input['name'] = 'ldap';			$input['id'] = 'ldap';			$input['value'] = '1';			$input['checked'] = @set_checkbox($input['name'], $input['value'], 				($user->ldap == $input['value']));			$input['tabindex'] = $t;			echo form_checkbox($input);			$t++;			?>			<span>LDAP</span>		</label>	</fieldset>	</div><hr><div class="alpha three columns"><h6>Membership</h6></div><div class="omega nine columns">	<label for="group_id">Group</label>	<?php	echo form_dropdown(		'group_id', 		$groups, 		set_value(			'group_id', 			(isset($user->group_id)) ? $user->group_id : 0		), 		'tabindex="' . $t . '" id="group_id"'	);	$t++;	?>		<label for="departments">Departments</label>	<select name="departments[]" id="departments" size="10" multiple="multiple" tabindex="<?php echo $t ?>">	<?php	foreach($departments as $did => $dname){		$selected = (in_array($did, $user->departments)) ? ' selected="selected"' : '';		echo '<option value="' . $did . '"' . $selected . '>' . $dname . '</option>';	}	$t++;	?>	</select></div><hr><div class="alpha three columns">&nbsp;</div><div class="omega nine columns"><?php$text = ($user_id == null) ? 'Add' : 'Save';unset($buttons);$buttons[] = array('submit', 'blue', "$text user", $t);$this->load->view('parts/buttons', array('buttons' => $buttons));?></div></form>