<?php echo form_open('authentication/save_global', array('id' => 'auth_global_form')) ?>			<fieldset class="form clearfix">				<legend>Authentication methods</legend>				<div class="inputs">					<div class="controls">								<?php $name = 'auth_ldap_enable'; $value = '1'; ?>				<?php echo form_hidden($name, '0'); ?>				<label class="check"><?php echo form_checkbox(array(					'name' => $name,					'id' => $name . '_1',					'tabindex' => tab_index(),					'value' => $value,					'checked' => set_checkbox($name, $value, (element($name, $settings) == $value)),				)) ?> Enable LDAP</label>							</div>						<div class="controls">								<?php $name = 'auth_preauth_enable'; $value = '1'; ?>				<?php echo form_hidden($name, '0'); ?>				<label class="check"><?php echo form_checkbox(array(					'name' => $name,					'id' => $name . '_1',					'tabindex' => tab_index(),					'value' => $value,					'checked' => set_checkbox($name, $value, (element($name, $settings) == $value)),				)) ?> Enable pre-authentication</label>							</div> <!-- / .controls -->				</div> <!-- / .inputs -->			</fieldset>			<fieldset class="form clearfix">			<legend>Anonymous user</legend>				<div class="inputs">			<?php			$name = 'auth_anon_u_id';			echo form_dropdown(				$name,				$users, set_value($name, element($name, $settings)),				'tabindex="' . tab_index() . '" id="' . $name . '"'			);			?>		</div>			</fieldset>			<fieldset class="form clearfix actions">		<?php echo form_button(array(			'type' => 'submit',			'class' => 'blue',			'text' => lang('ACTION_SAVE') . ' ' . strtolower(lang('W_SETTINGS')),			'tab_index' => tab_index(),		)) ?>	</fieldset>		</form>