<?php// ===== Methods$section = 'methods';$this->form->add_section($section, lang('authentication_methods'), lang('authentication_methods_hint'));	// ----- Enable LDAP	$name = 'auth_ldap_enable';	$value = '1';		$content = form_hidden($name, '0');	$content .= '<label class="check">';	$content .= form_checkbox(array(		'name' => $name,		'id' => $name . '_1',		'tabindex' => tab_index(),		'value' => $value,		'checked' => set_checkbox($name, $value, (element($name, $settings) == $value)),	));	$content .= lang('authentication_enable_ldap');	$content .= '</label>';			$this->form->add_input(array(		'section' => $section,		'name' => $name,		'content' => $content,	));			// ----- Enable Pre-authentication	$name = 'auth_preauth_enable';	$value = '1';		$content = form_hidden($name, '0');	$content .= '<label class="check">';	$content .= form_checkbox(array(		'name' => $name,		'id' => $name . '_1',		'tabindex' => tab_index(),		'value' => $value,		'checked' => set_checkbox($name, $value, (element($name, $settings) == $value)),	));	$content .= lang('authentication_enable_preauth');	$content .= '</label>';			$this->form->add_input(array(		'section' => $section,		'name' => $name,		'content' => $content,	));// ===== Anonymous user$section = 'anonymous_user';$this->form->add_section($section, lang('authentication_anonymous'), lang('authentication_anonymous_hint'));	// ----- Anonymous user	$name = 'auth_anon_u_id';		$this->form->add_input(array(				'section' => $section,		'name' => $name,				'content' => form_dropdown(			$name,			$users, set_value($name, element($name, $settings)),			'tabindex="' . tab_index() . '" id="' . $name . '"'		),			));		$this->form->add_button(form_button(array(	'type' => 'submit',	'class' => 'primary',	'text' => lang('save'),	'tab_index' => tab_index(),)));echo form_open(current_url(), array('id' => 'auth_global_form'));echo $this->form->render();echo '</form>';