<?php echo form_open('authentication/save_ldap', array('id' => 'auth_ldap_form')) ?>	<p class="hint large">If you enable LDAP authentication, users who attempt to login 	with LDAP credentials will be added to Classroombookings if successful. Ensure 	you set the Default group below to ensure they inherit the correct permissions.</p>		<fieldset class="form clearfix">				<legend>Server settings</legend>				<div class="inputs">						<?php $name = 'auth_ldap_host' ?>			<label for="<?php echo $name ?>">Hostname or IP address</label>			<?php echo form_input(array(				'name' => $name,				'id' => $name,				'class' => 'text-input',				'size' => 30,				'max_length' => 100,				'tabindex' => tab_index(),				'value' => set_value($name, element($name, $settings)),			)) ?>						<?php $name = 'auth_ldap_port' ?>			<label for="<?php echo $name ?>">Port number</label>			<?php echo form_input(array(				'name' => $name,				'id' => $name,				'class' => 'text-input',				'size' => 5,				'max_length' => 5,				'tabindex' => tab_index(),				'value' => set_value($name, element($name, $settings, '389')),			)) ?>						<?php $name = 'auth_ldap_base' ?>			<label for="<?php echo $name ?>">Base DNs</label>			<?php echo form_textarea(array(				'name' => $name,				'id' => $name,				'class' => 'text-input',				'rows' => 10,				'cols' => 60,				'tabindex' => tab_index(),				'value' => set_value($name, element($name, $settings)),			)) ?>						<?php $name = 'auth_ldap_filter' ?>			<?php $default = '(& (| (!(displayname=Administrator*)) (!(displayname=Admin*)) ) (cn=%u) )' ?>			<label for="<?php echo $name ?>">User query filter</label>			<?php echo form_textarea(array(				'name' => $name,				'id' => $name,				'class' => 'text-input half-bottom',				'rows' => 10,				'cols' => 60,				'tabindex' => tab_index(),				'value' => set_value($name, element($name, $settings, $default)),			)) ?>						<div class="add-bottom">				<span class="hint"><strong>%u</strong> is the supplied username.</span>			</div>					</div> <!-- / .inputs -->			</fieldset> <!-- / .fieldset for server settings -->			<fieldset class="form clearfix">				<legend>Test settings</legend>				<div class="inputs">						<p class="hint large">Enter the credentials of an account on the LDAP server to test the above settings.</p>						<?php $name = 'ldap_test_username' ?>			<label for="<?php echo $name ?>">Username</label>			<?php echo form_input(array(				'name' => $name,				'id' => $name,				'class' => 'text-input',				'size' => 30,				'max_length' => 100,				'autocomplete' => 'off',				'tabindex' => tab_index(),				'value' => set_value($name),			)) ?>						<?php $name = 'ldap_test_password' ?>			<label for="<?php echo $name ?>">Password</label>			<?php echo form_password(array(				'name' => $name,				'id' => $name,				'class' => 'text-input',				'size' => 30,				'max_length' => 100,				'autocomplete' => 'off',				'tabindex' => tab_index(),			)) ?>						<?php echo form_button(array(				'type' => 'button',				'id' => 'test_ldap',				'class' => 'grey half-bottom',				'text' => 'Test',				'tab_index' => tab_index(),			)) ?>						<div id="ldap_test_response" class="hidden">				<h6>LDAP server response</h6>				<p class="response-text"></p>			</div>						</div>			</fieldset> <!-- / .fieldset for testing -->			<fieldset class="form clearfix">				<legend>Integration</legend>				<div class="inputs">						<?php $name = 'auth_ldap_g_id' ?>			<label for="<?php echo $name ?>">Default Classroombookings group</label>			<?php echo form_dropdown(				$name, 				$groups, 				set_value($name, element($name, $settings)), 				'tabindex="' . tab_index() . '" id="' . $name . '"'			) ?>						<?php $name = 'auth_ldap_update'; $value = '1'; ?>			<?php echo form_hidden($name, '0'); ?>			<label class="check"><?php echo form_checkbox(array(				'name' => $name,				'id' => $name . '_1',				'tabindex' => tab_index(),				'value' => $value,				'checked' => set_checkbox($name, $value, (element($name, $settings) == $value)),			)) ?> Update details on every login</label>						<p class="hint light">If this option is enabled, the user details 			(display name, group and department membership) will be updated with 			their LDAP details every time they login. If you manually change any of 			these properties for a user, they may be un-done.</p>			<br>					</div>			</fieldset> <!-- / .fieldset for integration -->			<fieldset class="form clearfix actions">		<?php echo form_button(array(			'type' => 'submit',			'class' => 'blue',			'text' => lang('ACTION_SAVE') . ' LDAP ' . strtolower(lang('W_SETTINGS')),			'tab_index' => tab_index(),		)) ?>	</fieldset></form>